function executeSkill(context) {
  // 使用teleport函数进行传送
  teleport();

  // 直接修改自身的攻击力（buff效果）
  const buffValue = 15;
  sourcePiece.attack += buffValue;

  // 直接修改范围内敌人的生命值（damage效果）
  const damageValue = sourcePiece.attack * 2.5;
  const enemies = select.getEnemiesInRange(5);
  enemies.forEach(enemy => {
    enemy.currentHp = Math.max(0, enemy.currentHp - damageValue);
  });

  return { 
    message: sourcePiece.templateId + ' 激活了奥术爆发，提升攻击力' + buffValue + '点，对' + enemies.length + '个敌人造成' + damageValue + '点伤害', 
    success: true 
  };
}

