function calculatePreview(piece, skillDef) {
  // 计算预期伤害值和增益值
  const buffValue = 15;
  const damageValue = Math.round((piece.attack + buffValue) * 2.5); // 考虑增益后的攻击力
  const areaSize = skillDef.areaSize || 5;

  return {
    description: "传送到目标位置，提升自身攻击力" + buffValue + "点，对" + areaSize + "格范围内的所有敌人造成" + damageValue + "点伤害",
    expectedValues: {
      damage: damageValue,
      buff: buffValue
    }
  };
}
