function calculatePreview(piece, skillDef) {
  // 计算预期伤害值
  const damageValue = Math.round(piece.attack * skillDef.powerMultiplier);

  return {
    description: "选择最近的敌人，造成" + damageValue + "点伤害（相当于攻击力100%）",
    expectedValues: {
      damage: damageValue
    }
  };
}
