function executeSkill(context) {
  if (!context.piece) {
    return { message: '没有目标', success: false };
  }
  // 检查statusTags中的冰冻状态
  if (context.piece.statusTags) {
    const hasFreezeTag = context.piece.statusTags.some(tag => {
      if (typeof tag === 'string') {
        return tag === 'freeze';
      }
      if (typeof tag === 'object' && tag !== null) {
        return tag.type === 'freeze';
      }
      return false;
    });
    if (hasFreezeTag) {
      // 根据触发类型确定行动类型
      let actionType = '执行该操作';
      if (context.type === 'beforeMove') {
        actionType = '移动';
    } else if (context.type === 'beforeSkillUse') {
        actionType = '使用技能';
      }
      return { message: context.piece.name + '被冰冻，无法' + actionType, success: true, blocked: true };
    }
  }
  return { message: '目标未被冰冻或行动未被阻止', success: true };
}
