// 悍猛格挡状态效果函数
function EffectTrigger(context) {
  // 这个状态效果不需要每回合触发，只需要在受到伤害时触发
  // 持续时间由系统自动处理
  return { success: true, message: context.piece.templateId + '的悍猛格挡状态生效中' }
}

// 当受到伤害时触发的函数
function onDamageReceived(context) {
  const damage = context.damage;
  const attackBonus = Math.floor(damage * 0.5);
  context.piece.attack += attackBonus;
  return {
    success: true,
    message: context.piece.templateId + '触发了悍猛格挡，将' + attackBonus + '点伤害转化为攻击力',
    damage: damage // 返回原始伤害值
  }
}
