debuff-defense.code:
function executeSkill(context) {
  if (!context.target) {
    return {
      message: "没有目标",
      success: false
    }
  }
  return {
    effects: [{
      type: "debuff",
      value: 5,
      target: "enemy",
      duration: 2,
      description: "防御力降低5点，持续2回合"
    }],
    message: "防御削弱生效，降低目标防御力",
    success: true
  }
}
